function getRandomFact() {
    document.body.style.overflow = "hidden";
    let randomIndex = Math.floor(Math.random() * poppyFacts.length);
    let fact = poppyFacts[randomIndex];
    
    document.getElementById('modalFactText').innerHTML = fact;
    document.getElementById('factModal').style.display = 'block';
}

function closeModal() {
    document.body.style.overflow = "auto";
    let modal = document.getElementById('factModal');
    let modalContent = modal.querySelector('.modal-content');
    
    modalContent.classList.add('modal-disappear');
    modal.style.background = 'rgba(0, 0, 0, 0)';
    modal.style.transition = 'background 0.3s ease';
    
    setTimeout(function() {
        modal.style.display = 'none';
        modal.style.background = 'rgba(0, 0, 0, 0.8)';
        modalContent.classList.remove('modal-disappear');
    }, 300);
}

